import React, {useState} from 'react';

import Menu from '../components/Menu';

import { Jumbotron, Container, Button, Form, FormGroup, Label, Input, Alert } from 'reactstrap';


function Home({data}) {
    return (
        <div>
            <Menu />
            <Jumbotron fluid>
                <Container>
                <h1>Painel de Metas</h1>
                {data.metas.map(meta => (
                    <div key={meta._id}>
                        <h2>{meta.name}</h2>
                        <p>{meta.description}</p>
                        <p>{meta.status}</p>
                        <hr />
                    </div>
                ))}
                </Container>
            </Jumbotron>
        </div>
    )
}


export async function getServerSideProps() {
    const response = await fetch(`http://localhost:8080/metas`)
    const data = await response.json();
    //console.log(data);
    return { props: {data} };
}


export default Home;
