import React, {useState} from 'react';

import Menu from '../components/Menu';

import { Jumbotron, Container, ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText } from 'reactstrap';


function Home({data}) {
    return (
        <div>
            <Menu />
            <Jumbotron fluid className="list">
                <style>
                    {`.list{
                        background-color: #171941;
                        color: #bf38ac;
                        padding-top: 30px;
                        padding-bottom: 150px;
                        margin-bottom: 0rem !important;
                    }`}
                </style>
                <Container>
                <h1 className="display-4 text-center">Painel de Metas</h1>

                    <ListGroup>
                    {data.metas.map(meta => (
                        <div key={meta._id}>
                            <h2>{meta.name}</h2>
                            <p>{meta.description}</p>
                            <p>{meta.status}</p>
                            <hr />
                        </div>
                    ))}
                    </ListGroup>
                </Container>
            </Jumbotron>
        </div>
    )
}


export async function getServerSideProps() {
    const response = await fetch(`http://localhost:8080/metas`)
    const data = await response.json();
    //console.log(data);
    return { props: {data} };
}


export default Home;
